Update SIGA.EX_TIPO_DOCUMENTO set DESCR_TIPO_DOCUMENTO = 'Produzido' where ID_TP_DOC = 1 AND EXISTS(SELECT NM_ORGAO_USU FROM CORPORATIVO.CP_ORGAO_USUARIO WHERE NM_ORGAO_USU = 'Prodesp');
Update SIGA.EX_TIPO_DOCUMENTO set DESCR_TIPO_DOCUMENTO = 'Capturado' where ID_TP_DOC = 4 AND EXISTS(SELECT NM_ORGAO_USU FROM CORPORATIVO.CP_ORGAO_USUARIO WHERE NM_ORGAO_USU = 'Prodesp');